DROP TABLE tbproveedores;
CREATE TABLE tbproveedores (
  IdProveedor AUTOINC NOT NULL,
  Codigo INTEGER,
  Nombre VARCHAR(50),
  Contacto VARCHAR(50),
  Direccion VARCHAR(50),
  Localidad VARCHAR(30),
  Provincia VARCHAR(30),
  CP VARCHAR(8),
  Telefono VARCHAR(50),
  Celular VARCHAR(30),
  TipoIva SMALLINT,
  CUIT VARCHAR(14),
  Observaciones MEMO,
  Web VARCHAR(50),
  Mail VARCHAR(50),
  Rubro VARCHAR(20),
  Saldo FLOAT,
  Pais VARCHAR(30),
  Fax VARCHAR(20),
  FechaModif DATE, 
  INDEX Nombre (Nombre), 
  INDEX Codigo (Codigo), 
  INDEX Direccion (Direccion), 
  INDEX Cuit (CUIT), 
  INDEX Contacto (Contacto), 
  INDEX Teléfono (Telefono), 
  INDEX Id (IdProveedor)
);

